
exports.cssStringToColor = function(colorString) {
    const firstChar = colorString.substr(0, 1) === "#";
    let hexToRGB = function(rgb){
        const r = rgb >> 16;
        const g = rgb >> 8 & 0xFF;
        const b = rgb & 0xFF;
        return RGBtoInt(r,g,b);
    }
    let RGBtoInt = function(r,g,b){
        return (b * 256 * 256) + (g * 256) + r;
    }
    if (firstChar){
        let rgb = colorString.slice(1,colorString.length );
        if (colorString.length == 4){
            rgb = rgb[0] + rgb[0] + rgb[1] + rgb[1] + rgb[2] +rgb[2];
        }
        const hexToInt = hexToRGB(parseInt(rgb, 16));
        return hexToInt;
    }
    else {
        throw "Error";
    }
}
